import { CommonModule } from '@angular/common';
import { Component } from '@angular/core';

@Component({
  selector: 'app-services',
  standalone:true,
  imports:[CommonModule],
  templateUrl: './services.component.html',
  styleUrls: ['./services.component.scss']
})
export class ServicesComponent {

  services = [
  {
    title: 'Software Development',
    desc: 'Scalable custom software built for performance and growth.',
    icon: 'assets/icons/software.svg'
  },
  {
    title: 'Web Development',
    desc: 'High-performance web applications with modern UX.',
    icon: 'assets/icons/web.svg'
  },
  {
    title: 'Mobile App Development',
    desc: 'iOS & Android apps built with scalability in mind.',
    icon: 'assets/icons/mobile.svg'
  },
  {
    title: 'SaaS Development',
    desc: 'Secure and scalable SaaS platforms.',
    icon: 'assets/icons/saas.svg'
  },
  {
    title: 'UI/UX Design',
    desc: 'User-first design for better engagement.',
    icon: 'assets/icons/design.svg'
  },
  {
    title: 'DevOps & Cloud',
    desc: 'CI/CD, cloud infra, and monitoring.',
    icon: 'assets/icons/devops.svg'
  }
];
steps = [
  { id: 1, title: 'Requirement Analysis', desc: 'Understanding your needs.' },
  { id: 2, title: 'Planning', desc: 'Roadmap & timelines.' },
  { id: 3, title: 'Development', desc: 'Agile implementation.' },
  { id: 4, title: 'Launch & Support', desc: 'Go-live & maintenance.' }
];

}
<section class="service-hero">
  <div class="service-hero__container">

    <h1 class="service-title">
      Software Consulting <span>Services</span>
    </h1>

    <p class="service-subtitle">
      We help businesses make the right technology decisions,
      reduce risk, and build scalable digital solutions
      with expert software consulting.
    </p>

    <div class="service-actions">
      <a routerLink="/lets-talk" class="btn-primary">
        Talk to Our Experts →
      </a>

      <a routerLink="/services" class="btn-secondary">
        Explore All Services
      </a>
    </div>

  </div>
</section>
