import { Component } from '@angular/core';

@Component({
  selector: 'app-services',
  templateUrl: './services.component.html',
  styleUrls: ['./services.component.scss']
})
export class ServicesComponent {

}
<section class="service-hero">
  <div class="container">
    <h1>Custom Software Services That Scale Your Business</h1>
    <p>
      We design, build, and scale digital products using modern technologies
      tailored to your business goals.
    </p>

    <a routerLink="/lets-talk" class="btn-primary">
      Let’s Talk
    </a>
  </div>
</section>
