import { CommonModule } from '@angular/common';
import { Component, ElementRef, ViewChild } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-services',
  standalone:true,
  imports:[CommonModule],
  templateUrl: './services.component.html',
  styleUrls: ['./services.component.scss']
})
export class ServicesComponent {

  services = [
    {
      title: 'Software Development',
      description: 'Custom, scalable, and secure software solutions.',
      icon: 'üß©',
      slug: 'software-development'
    },
    {
      title: 'Website Development',
      description: 'High-performance websites with strong UX.',
      icon: 'üåê',
      slug: 'website-development'
    },
    {
      title: 'Front-End Development',
      description: 'Interactive and user-focused UI development.',
      icon: 'üé®',
      slug: 'frontend-development'
    },
    {
      title: 'Back-End Development',
      description: 'Robust backend systems built for scale.',
      icon: '‚öôÔ∏è',
      slug: 'backend-development'
    },
    {
      title: 'Cloud & DevOps',
      description: 'Cloud-native deployments and CI/CD pipelines.',
      icon: '‚òÅÔ∏è',
      slug: 'cloud-devops'
    }
  ];

  constructor(private router: Router) {}

  goToService(slug: string) {
    this.router.navigate(['/services', slug]);
  }
  @ViewChild('carousel', { static: false })
  carousel!: ElementRef<HTMLDivElement>;

  cards = [
    {
      title: 'Customized Dining',
      description:
        'AI delivers personalized menus, dynamic pricing, and tailored offers for customers.'
    },
    {
      title: 'Streamlined Operations',
      description:
        'Automated inventory, smart reordering, and staff optimization reduce waste.'
    },
    {
      title: 'Customer Support',
      description:
        'AI chatbots provide instant help while sentiment analysis improves service.'
    },
    {
      title: 'Predictive Solutions',
      description:
        'AI forecasts demand, prevents downtime, and controls operational costs.'
    },
    {
      title: 'Data-Driven Insights',
      description:
        'Advanced analytics help owners make faster and smarter decisions.'
    }
  ];

  scrollLeft() {
    this.carousel.nativeElement.scrollBy({
      left: -300,
      behavior: 'smooth'
    });
  }

  scrollRight() {
    this.carousel.nativeElement.scrollBy({
      left: 300,
      behavior: 'smooth'
    });
  }


}
<section class="service-hero">
  <div class="service-hero__container">

    <h1 class="service-title">
      Software<span>Services</span>
    </h1>

    <p class="service-subtitle">
      We help businesses make the right technology decisions,
      reduce risk, and build scalable digital solutions
      with expert software consulting.
    </p>

    <div class="service-actions">
      <a routerLink="/lets-talk" class="btn-primary">
        Talk to Our Experts ‚Üí
      </a>

      <a routerLink="/services" class="btn-secondary">
        Explore All Services
      </a>
    </div>

  </div>
</section>
<section class="services-section">
  <div class="services-header">
    <span class="badge">Our Services</span>

    <div class="header-row">
      <h2>
        Explore proven IT solutions designed to accelerate your growth,
        efficiency, and digital transformation journey.
      </h2>

      <a class="cta-btn" routerLink="/lets-talk">
        Get a Custom Proposal ‚Üí
      </a>
    </div>
  </div>

  <div class="services-cards">
    <div
      class="service-card"
      *ngFor="let service of services"
      (click)="goToService(service.slug)"
    >
      <div class="icon">{{ service.icon }}</div>

      <h3>{{ service.title }}</h3>
      <p>{{ service.description }}</p>

      <span class="arrow">+</span>
    </div>
  </div>
</section>
<section class="ai-carousel-section">
  <h2>AI-Powered Restaurant App ‚Äì Core Benefits</h2>
  <p class="subtitle">
    Transform operations, improve customer experience, and increase revenue
    with intelligent automation.
  </p>

  <div class="carousel-wrapper">
    <!-- LEFT BUTTON -->
    <button class="nav-btn left" (click)="scrollLeft()">‚Äπ</button>

    <!-- CARD TRACK -->
    <div class="carousel-track" #carousel>
      <div
        class="carousel-card"
        *ngFor="let item of cards"
      >
        <h3>{{ item.title }}</h3>
        <p>{{ item.description }}</p>
      </div>
    </div>

    <!-- RIGHT BUTTON -->
    <button class="nav-btn right" (click)="scrollRight()">‚Ä∫</button>
  </div>
</section>
